<?php

if(true) // get $LINE
{//{{{
	if(!isset($_GET['file'])) {
		trigger_error("\$_GET['file'] is not set", E_USER_ERROR);
		exit(255);
	}
	
	if(!is_string($_GET['file'])) {
		trigger_error("\$_GET['file'] is not string", E_USER_ERROR);
		exit(255);
	}
	
	$file_name = $_GET['file'];
	
	if(!file_exists($file_name)) {
		trigger_error("file not exists", E_USER_ERROR);
		exit(255);
	}
	
	if(!is_file($file_name)) {
		trigger_error("file is not file", E_USER_ERROR);
		exit(255);
	}
	
	if(!is_readable($file_name)) {
		trigger_error("file is not readable", E_USER_ERROR);
		exit(255);
	}
	
	$LINE = file($file_name, FILE_IGNORE_NEW_LINES);
}//}}}

if(true) // create $table
{//{{{
	$table = '<table name="listing" class="source">';
	foreach($LINE as $index => $line) {
		$number = strval($index+1);
		$line = htmlentities($line);
		$line = preg_replace( "/ /", '&nbsp;', $line);
		$line = preg_replace( "/\t/", '&nbsp;&nbsp;&nbsp;&nbsp;', $line );
		$tr = 
<<<HEREDOC
<tr>
	<td id="{$number}" name="number" class="source">{$number}</td>
	<td name="line" class="source">{$line}</td>
</tr>
HEREDOC;
		$table .= $tr;
	}
	$table .= '</table>';
	//echo($table);
}//}}}

