if(true) // set $script
{//{{{

	$script =
////////////////////////////////////////////////////////////////////////////////
<<<'HEREDOC'
window.addEventListener("dblclick", function(event) // Double click selection with regular expression
{//{{{	
	let $string, $return;
	
	var $Selection = document.getSelection();
	if(!($Selection.type == "Range" && $Selection.rangeCount == 1)) {
		return(null);
	}

	var $Range = $Selection.getRangeAt(0);
	$Selection.removeRange($Range);
	
	var $RegExp = new RegExp("^[\-A-Za-z0-9~/\.&\$_:>]+$");
	
	while(true) {
		var $startOffset = $Range.startOffset - 1;
		if($startOffset >= 0) {
			$Range.setStart($Range.startContainer, $startOffset);
		}
		else {
			break;
		}
		
		$string = $Range.toString();
		$return = $RegExp.test($string);
		if($return == false) {
			$startOffset += 1;
			$Range.setStart($Range.startContainer, $startOffset);
			break;
		}
	}
	
	while(true) {
		$endOffset = $Range.endOffset + 1;
		if($endOffset < $Range.endContainer.length) {
			$Range.setEnd($Range.endContainer, $endOffset);
		}
		else {
			break;
		}
		
		$string = $Range.toString();
		$return = $RegExp.test($string);
		if($return == false) {
			$endOffset -= 1;
			$Range.setEnd($Range.endContainer, $endOffset);
			break;
		}
	}
	
	//console.log($Range.toString());
	$Selection.addRange($Range);
});//}}}

HEREDOC;
////////////////////////////////////////////////////////////////////////////////

	$tmp_file_name = addcslashes($file_name, "\\'\x00\n\r");
	$script .= 
<<<HEREDOC
var file_name = '{$tmp_file_name}';
var goToLine = {
	dialog: null,
	input: null,
	onKeyDown: function(event) 
	{//{{{
		if(!(
			event.key == 'Enter'	
			&& !event.ctrlKey 
			&& !event.altKey 
			&& !event.shiftKey
		)) {
			return;
		}
		
		let s = goToLine.input.value;
		if(!(s.length > 0)) {
			return;
		}
		
		const re = new RegExp('^[0-9]+$');
		if(!(re.test(s))) {
			return;
		}
		
		window.location.hash = "#" + s;
		goToLine.dialog.close();
	},//}}}
}

window.addEventListener('load', start);
window.addEventListener('keydown', keyboard);

function start(event)
{//{{{
	goToLine.dialog = document.getElementById("7648");
	goToLine.input = document.getElementById("80O1");
	goToLine.input.addEventListener('keydown', goToLine.onKeyDown);
	
	let table = document.querySelector('table[name="listing"]');
	let TD = table.querySelectorAll('td[name="number"]');
	let i = 0, l = TD.length;
	for(i = i; i < l; i += 1) {
		let td = TD[i];
		td.addEventListener('click', file_name_with_line_number_to_clipboard.bind(null, td.id));
	}
}//}}}

function file_name_with_line_number_to_clipboard(line_number)
{
	navigator.clipboard.writeText(file_name + ":" + line_number);
}

function keyboard(event)
{//{{{
	if(!event.ctrlKey && event.altKey && !event.shiftKey) {
		if(event.key == 'g' || event.key == 'п') {
			goToLine.dialog.showModal();
			goToLine.input.value = '';
			goToLine.input.focus();
			return;
		}
		if(event.key == 'n' || event.key == 'т') {
			navigator.clipboard.writeText(file_name);
			return;
		}
	}
}//}}}

HEREDOC;
	unset($tmp_file_name);
}//}}}

$head = "";

